import React, { useState, useEffect } from 'react';
import './App.css';

function App() {
  const [isLoading, setIsLoading] = useState(true);

  useEffect(() => {
    setTimeout(() => {
      setIsLoading(false);
    }, 2000);
  }, []);

  const [developerText, setDeveloperText] = useState("I am a ");
const [isTyping, setIsTyping] = useState(true);

const defaultText = "I am a ";
const words = ["Front-end Developer", "Back-end Developer", "Full-stack Developer", "UI/UX Designer"];
const currentWordIndex = useRef(0);  // use useRef here
const currentIndex = useRef(defaultText.length);  // use useRef here

useEffect(() => {
    function typeText() {
        if (isTyping) {
            const currentWord = words[currentWordIndex.current];  // use .current here
            if (currentIndex.current <= defaultText.length + currentWord.length) {
                setDeveloperText(defaultText + currentWord.slice(0, currentIndex.current - defaultText.length));
                currentIndex.current++;  // use .current here
                setTimeout(typeText, 200);
            } else {
                setIsTyping(false);
            }
        }
    }

    function deleteText() {
        if (!isTyping) {
            const currentWord = words[currentWordIndex.current];  // use .current here
            if (currentIndex.current > defaultText.length) {
                setDeveloperText(defaultText + currentWord.slice(0, currentIndex.current - defaultText.length - 1));
                currentIndex.current--;  // use .current here
                setTimeout(deleteText, 50);
            } else {
                setIsTyping(true);
                currentWordIndex.current = (currentWordIndex.current + 1) % words.length;  // use .current here
            }
        }
    }

    if (isTyping) {
        typeText();
    } else {
        deleteText();
    }

}, [isTyping]);



  return (
    <div className="app">
      {isLoading ? (
        <div className="loading-spinner">
          <div className="spinner"></div>
        </div>
      ) : (
        <div className='main'>
          <div className='first'>
            <div className='firstTop'>
              <div className='logo'>
                <p>SHUYA</p>
              </div>

              <div className='sideBarIcon'>
                <i className="fa-solid fa-bars"></i>
              </div>  
            </div>

            <div className='introduction'>
              <p>Hello,</p>
              <p>I'm Shuya Ikeo</p>
              <p className={isTyping ? 'typing' : ''}>
                {developerText}
              </p>

            </div>

            <div className='sideBar'>
            </div> 
          </div>

          <div className='page'>
            <div className='second'>
              <h1>Welcome to My Website</h1>
              <p>This is the content of my website.</p>
            </div>
          </div>
          <div className='page'>
            <div className='third'></div>
          </div>
          <div className='page'>
            <div className='fourth'></div>
          </div>
        </div>
      )}
    </div>
  );
}

export default App;
