import React, { useState, useEffect } from 'react';
import './App.css';
import { useRef } from 'react';

function App() {
  const [isLoading, setIsLoading] = useState(true);

  useEffect(() => {
    setTimeout(() => {
      setIsLoading(false);
    }, 2000);
  }, []);

  const [text, setText] = useState("I am a ");
  const [isTyping, setIsTyping] = useState(true);

  const defaultText = "I am a ";
  const words = ["Front-end Developer.", "Back-end Developer.", "Full-stack Developer.", "UI/UX Designer."];
  const currentWordIndex = useRef(0);  
  const currentIndex = useRef(defaultText.length);  

  useEffect(() => {
      function typeText() {
          if (isTyping) {
              const currentWord = words[currentWordIndex.current];  
              if (currentIndex.current <= defaultText.length + currentWord.length) {
                  setText(defaultText + currentWord.slice(0, currentIndex.current - defaultText.length));
                  currentIndex.current++;  
                  setTimeout(typeText, 200);
              } else {
                setTimeout(() => {
                  setIsTyping(false);
              }, 1000);
              }
          }
      }

      function deleteText() {
          if (!isTyping) {
              const currentWord = words[currentWordIndex.current];  
              if (currentIndex.current > defaultText.length) {
                  setText(defaultText + currentWord.slice(0, currentIndex.current - defaultText.length - 1));
                  currentIndex.current--; 
                  setTimeout(deleteText, 50);
              } else {
                  setIsTyping(true);
                  currentWordIndex.current = (currentWordIndex.current + 1) % words.length; 
              }
          }
      }

      if (isTyping) {
          typeText();
      } else {
          deleteText();
      }

  }, [isTyping]);


  const [isSidebarOpen, setIsSidebarOpen] = useState(false);

  const homeRef = useRef(null);
  const aboutMeRef = useRef(null);
  const educationRef = useRef(null);
  const projectsRef = useRef(null);
  const contactRef = useRef(null);

  const [activePage, setActivePage] = useState("home");

  useEffect(() => {
    const checkActivePage = () => {
      const homePosition = homeRef.current.getBoundingClientRect();
      if (homePosition.top <= 0 && homePosition.bottom >= 0){
        setActivePage("home");
      }else{
        setActivePage("others");
      }
    }

    window.addEventListener("scroll", checkActivePage);
    console.log(homePosition.top, homePosition.bottom);

    return () => {
      window.removeEventListener("scroll", checkActivePage);
    }
  },[])

  console.log(activePage);


  return (
    <div className="app">
      {isLoading ? (
        <div className="loading-spinner">
          <div className="spinner"></div>
        </div>
      ) : (
        <div className='main'>
          <div className={`header ${activePage}`}>
            <div className='logo'>
              <p>SHUYA</p>
            </div>

            <div className='sideBarIcon'>
              <i className="fa-solid fa-bars" onClick={() => setIsSidebarOpen(true)}></i>
            </div>  
          </div>
          <div className={`sideBar ${isSidebarOpen ? "open" : ""}`}>
            <div className='closeIcon'>
              <i class="fa-solid fa-xmark" onClick={() => setIsSidebarOpen(false)}></i>
            </div>
            <div className='list'>
              <p onClick = {() => homeRef.current.scrollIntoView({behavior: "smooth"})}>HOME</p>
              <p onClick = {() => aboutMeRef.current.scrollIntoView({behavior: "smooth"})}>ABOUT ME</p>
              <p onClick = {() => educationRef.current.scrollIntoView({behavior: "smooth"})}>EDUCATION</p>
              <p onClick = {() => projectsRef.current.scrollIntoView({behavior: "smooth"})}>PROJECTS</p>
              <p onClick = {() => contactRef.current.scrollIntoView({behavior: "smooth"})}>CONTACT</p>
            </div>
            <div className='email'>
              <p>shuya.ikeo@gmail.com</p>
            </div>
          </div>

          <div ref={homeRef} className='first'>

            <div className='introduction'>
              <p>Hello,</p>
              <p>I'm Shuya Ikeo</p>
              <p>I am a <span className= "typing">{text.replace(defaultText, "")}</span></p>

            </div> 
          </div>


          <div ref={aboutMeRef} className='second'>
            <h1>Welcome to My Website</h1>
            <p>This is the content of my website.</p>
          </div>


          <div ref={educationRef} className='third'>

          </div>


          <div ref={projectsRef} className='fourth'>

          </div>

          <div ref={contactRef} className='fifth'>

          </div>

        </div>
      )}
    </div>
  );
}

export default App;
