import React, { useState, useEffect, useRef } from 'react';
import './App.css';
import Scrollspy from 'react-scrollspy';

function App() {
    const [isLoading, setIsLoading] = useState(true);
    const [text, setText] = useState("I am a ");
    const [isTyping, setIsTyping] = useState(true);
    const defaultText = "I am a ";
    const words = ["Front-end Developer.", "Back-end Developer.", "Full-stack Developer.", "UI/UX Designer."];
    const currentWordIndex = useRef(0);  
    const currentIndex = useRef(defaultText.length);
    const [isSidebarOpen, setIsSidebarOpen] = useState(false);
    const [activePage, setActivePage] = useState("home");

    useEffect(() => {
        setTimeout(() => {
            setIsLoading(false);
        }, 2000);
    }, []);

    useEffect(() => {
        function typeText() {
            if (isTyping) {
                const currentWord = words[currentWordIndex.current];  
                if (currentIndex.current <= defaultText.length + currentWord.length) {
                    setText(defaultText + currentWord.slice(0, currentIndex.current - defaultText.length));
                    currentIndex.current++;  
                    setTimeout(typeText, 200);
                } else {
                    setTimeout(() => {
                        setIsTyping(false);
                    }, 1000);
                }
            }
        }

        function deleteText() {
            if (!isTyping) {
                const currentWord = words[currentWordIndex.current];  
                if (currentIndex.current > defaultText.length) {
                    setText(defaultText + currentWord.slice(0, currentIndex.current - defaultText.length - 1));
                    currentIndex.current--; 
                    setTimeout(deleteText, 50);
                } else {
                    setIsTyping(true);
                    currentWordIndex.current = (currentWordIndex.current + 1) % words.length; 
                }
            }
        }

        if (isTyping) {
            typeText();
        } else {
            deleteText();
        }
    }, [isTyping]);

    const [activeProjectIndex, setActiveProjectIndex] = useState(1);

useEffect(() => {
    const projects = document.querySelector(".projects");

    if (activeProjectIndex === 0) {
        projects.style.transform = "translateX(12rem)";
    } else if (activeProjectIndex === 2) {
        projects.style.transform = "translateX(-12rem)";
    } else {
        projects.style.transform = "translateX(0)";
    }
}, [activeProjectIndex]);


    return (
        <div className="app">
            {isLoading ? (
                <div className="loading-spinner">
                    <div className="spinner"></div>
                </div>
            ) : (
                <div className='main'>
                    <div className={`header ${activePage}`}>
                        <div className='logo'>
                            <p>SHUYA</p>
                        </div>
                        <div className='sideBarIcon'>
                            <i className="fa-solid fa-bars" onClick={() => setIsSidebarOpen(true)}></i>
                        </div>  
                    </div>

                    <div className={`sideBar ${isSidebarOpen ? "open" : ""}`}>
                        <div className='closeIcon'>
                            <i className="fa-solid fa-xmark" onClick={() => setIsSidebarOpen(false)}></i>
                        </div>
                        <Scrollspy 
                            items={['first', 'second', 'third', 'fourth', 'fifth']} 
                            currentClassName="is-current"
                            offset={-200}
                            onUpdate={(item) => {
                                if (item && item.id) {
                                    setActivePage(item.id === "first" ? "home" : "others");
                                }
                            }}>
                            <p onClick={() => document.getElementById("first").scrollIntoView({ behavior: "smooth" })}>HOME</p>
                            <p onClick={() => document.getElementById("second").scrollIntoView({ behavior: "smooth" })}>ABOUT ME</p>
                            <p onClick={() => document.getElementById("third").scrollIntoView({ behavior: "smooth" })}>EDUCATION</p>
                            <p onClick={() => document.getElementById("fourth").scrollIntoView({ behavior: "smooth" })}>PROJECTS</p>
                            <p onClick={() => document.getElementById("fifth").scrollIntoView({ behavior: "smooth" })}>CONTACT</p>
                        </Scrollspy>
                        <div className='email'>
                            <p>shuya.ikeo@gmail.com</p>
                        </div>
                    </div>

                    <div id="first" className='first'>
                        <div className='introduction'>
                            <p>Hello,</p>
                            <p>I'm Shuya Ikeo</p>
                            <p>I am a <span className= "typing">{text.replace(defaultText, "")}</span></p>
                        </div> 
                    </div>
                    <div id="second" className='second'>
                        <h1>Welcome to My Website</h1>
                        <p>This is the content of my website.</p>
                    </div>
                    <div id="third" className='third'></div>
                    <div id="fourth" className='fourth'>
                      <div className='projectContaine'>
                        <div className='projects'>
                          <div className='project' onClick={() => setActiveProjectIndex(0)}>
                            Project1
                          </div>
                          <div className='project' onClick={() => setActiveProjectIndex(1)}>
                            Project2
                          </div>
                          <div className='project' onClick={() => setActiveProjectIndex(2)}>
                            Project3
                          </div>
                        </div>   
                      </div>
                    </div>
                    <div id="fifth" className='fifth'></div>
                </div>
            )}
        </div>
    );
}

export default App;
