import React, { useState, useEffect } from 'react';
import './App.css';

function App() {
  const [isLoading, setIsLoading] = useState(true);
  const [developerText, setDeveloperText] = useState('');
  const [isTyping, setIsTyping] = useState(true);

  useEffect(() => {
    setTimeout(() => {
      setIsLoading(false);
    }, 2000);
  }, []);

  useEffect(() => {
    const text = "I AM A Passionate Developer";
    let currentIndex = 0;

    function typeText() {
      if (isTyping) {
        setDeveloperText(text.substring(0, currentIndex));
        currentIndex++;
        if (currentIndex > text.length) {
          setIsTyping(false);
          currentIndex = text.length;
        }
      } else {
        setDeveloperText(text.substring(currentIndex-1, currentIndex));
        currentIndex--;
        if (currentIndex < 0) {
          setIsTyping(true);
          currentIndex = 0;
        }
      }
    }

    const typingInterval = setInterval(typeText, 150); // Adjust the typing speed as needed

    return () => {
      clearInterval(typingInterval);
    };
  }, [isTyping]);

  return (
    <div className="app">
      {isLoading ? (
        <div className="loading-spinner">
          <div className="spinner"></div>
        </div>
      ) : (
        <div className='main'>
          <div className='first'>
            <div className='firstTop'>
              <div className='logo'>
                <p>SHUYA</p>
              </div>

              <div className='sideBarIcon'>
                <i className="fa-solid fa-bars"></i>
              </div>  
            </div>

            <div className='introduction'>
              <p>Hello,</p>
              <p>I'm Shuya Ikeo</p>
              <p>
                {developerText}
                <span id="cursor" className={isTyping ? 'blink' : ''}></span>
              </p>
            </div>

            <div className='sideBar'>
            </div> 
          </div>

          <div className='page'>
            <div className='second'>
              <h1>Welcome to My Website</h1>
              <p>This is the content of my website.</p>
            </div>
          </div>
          <div className='page'>
            <div className='third'></div>
          </div>
          <div className='page'>
            <div className='fourth'></div>
          </div>
        </div>
      )}
    </div>
  );
}

export default App;
