import React, { useState, useEffect } from 'react';
import './App.css';

function App() {
  const [isLoading, setIsLoading] = useState(true);
  const [developerText, setDeveloperText] = useState('');
  const [isTyping, setIsTyping] = useState(true);

  useEffect(() => {
    setTimeout(() => {
      setIsLoading(false);
    }, 2000);
  }, []);

  useEffect(() => {
    const defaultText = "I am a ";
    const words = ["Front-end Developer", "Back-end Developer", "Full-stack Developer", "UI/UX Designer"];
    let currentWordIndex = 0;

    function typeText() {
        if (isTyping) {
            const currentWord = words[currentWordIndex];
            if (currentIndex <= currentWord.length) {
                setDeveloperText(defaultText + currentWord.slice(0, currentIndex + 1));
                currentIndex++;
                setTimeout(typeText, 200);
            } else {
                setIsTyping(false);
                setTimeout(deleteText, 50);
            }
        }
    }

    function deleteText() {
        if (!isTyping) {
            const currentWord = words[currentWordIndex];
            if (currentIndex >= 0) {
                setDeveloperText(defaultText + currentWord.slice(0, currentIndex));
                currentIndex--;
                setTimeout(deleteText, 50);
            } else {
                setIsTyping(true);
                currentWordIndex = (currentWordIndex + 1) % words.length;  // cycle to the next word
                setTimeout(typeText, 200);
            }
        }
    }



    if (isTyping) {
      typeText();
    } else {
      deleteText();
    }

    return () => {};

  }, [isTyping]);


  return (
    <div className="app">
      {isLoading ? (
        <div className="loading-spinner">
          <div className="spinner"></div>
        </div>
      ) : (
        <div className='main'>
          <div className='first'>
            <div className='firstTop'>
              <div className='logo'>
                <p>SHUYA</p>
              </div>

              <div className='sideBarIcon'>
                <i className="fa-solid fa-bars"></i>
              </div>  
            </div>

            <div className='introduction'>
              <p>Hello,</p>
              <p>I'm Shuya Ikeo</p>
              <p className={isTyping ? 'typing' : ''}>
                {developerText}
              </p>

            </div>

            <div className='sideBar'>
            </div> 
          </div>

          <div className='page'>
            <div className='second'>
              <h1>Welcome to My Website</h1>
              <p>This is the content of my website.</p>
            </div>
          </div>
          <div className='page'>
            <div className='third'></div>
          </div>
          <div className='page'>
            <div className='fourth'></div>
          </div>
        </div>
      )}
    </div>
  );
}

export default App;
