import React, { useState, useEffect } from 'react';
import './App.css';

function App() {
  const [isLoading, setIsLoading] = useState(true);
  const [developerText, setDeveloperText] = useState('');
  const [isTyping, setIsTyping] = useState(true);

  useEffect(() => {
    setTimeout(() => {
      setIsLoading(false);
    }, 2000);
  }, []);

  useEffect(() => {
    const text = "I Am A Passionate Developer.";
    let currentIndex = developerText.length;

    function typeText() {
      if (isTyping) {
        setDeveloperText(prevText => prevText + text[currentIndex]);
        currentIndex++;

        if (currentIndex < text.length) {
          setTimeout(typeText, 200);
        } else {
          setIsTyping(false);
          setTimeout(deleteText, 50);
        }
      }
    }

    function deleteText() {
      if (!isTyping) {
        setDeveloperText(prevText => prevText.slice(0, -1));
        currentIndex--;

        if (currentIndex >= 0) {
          setTimeout(deleteText, 50);
        } else {
          setIsTyping(true);
          setTimeout(typeText, 200);
        }
      }
    }

    if (isTyping) {
      typeText();
    } else {
      deleteText();
    }

    return () => {};

  }, [isTyping]);


  return (
    <div className="app">
      {isLoading ? (
        <div className="loading-spinner">
          <div className="spinner"></div>
        </div>
      ) : (
        <div className='main'>
          <div className='first'>
            <div className='firstTop'>
              <div className='logo'>
                <p>SHUYA</p>
              </div>

              <div className='sideBarIcon'>
                <i className="fa-solid fa-bars"></i>
              </div>  
            </div>

            <div className='introduction'>
              <p>Hello,</p>
              <p>I'm Shuya Ikeo</p>
              <p className={isTyping ? 'typing' : ''}>
                {developerText}
              </p>

            </div>

            <div className='sideBar'>
            </div> 
          </div>

          <div className='page'>
            <div className='second'>
              <h1>Welcome to My Website</h1>
              <p>This is the content of my website.</p>
            </div>
          </div>
          <div className='page'>
            <div className='third'></div>
          </div>
          <div className='page'>
            <div className='fourth'></div>
          </div>
        </div>
      )}
    </div>
  );
}

export default App;
