import React, { useState, useEffect } from 'react';
import './App.css';

function App() {
  const [isLoading, setIsLoading] = useState(true);

  useEffect(() => {
    setTimeout(() => {
      setIsLoading(false); 
    }, 2000); 
  }, []);

  useEffect(() => {
    // Wrap the typewriter logic in a DOMContentLoaded event listener
    window.addEventListener('DOMContentLoaded', () => {
      const textElement = document.getElementById('developer-text');
      if (textElement) {
        const text = "I AM A Passionate Developer";
        let currentIndex = 0;
        let isDeleting = false;

        function typeText() {
          const currentText = text.substring(0, currentIndex);
          textElement.textContent = currentText;
          currentIndex = isDeleting ? currentIndex - 1 : currentIndex + 1;

          if (isDeleting && currentIndex === 0) {
            isDeleting = false;
          }

          if (currentIndex === text.length) {
            isDeleting = true;
          }

          const typingSpeed = isDeleting ? 50 : 150; 

          setTimeout(typeText, typingSpeed);
        }

        typeText();
      }
    });
  }, []);

  return (
    <div className="app">
      {isLoading ? (
        <div className="loading-spinner">
          <div className="spinner"></div>
        </div>
      ) : (
        <div className='main'>
          {/* ... Rest of your component */}
        </div>
      )}
    </div>
  );
}

export default App;
